INSERT INTO `bdd` (`scenario_id`, `given`, `given_and`, `whenn`, `when_and`, `thenn`, `then_and`, `scenario_desc`, `use_case_bdd`) VALUES
(1, 'new installation of IOT device', NULL, 'powering on a new IOT device in a existing IoT system', NULL, 'the default password must be changed', NULL, '1test', NULL),
(2, 'the distributiin of new firmware', NULL, 'updating the firmware of a IoT device', NULL, 'Passwords should not change', NULL, '2test', NULL),
(3, 'a connected IoT device operational within its specification.', 'not being the gateway or backend system', 'a malicious firmware update of a connected IoT device  ', 'not being the gateway or backend system', 'the update is noted as a malicious event and signaled to gateway and/or backend system', 'in case of a classxx device the device is immediately demoted', 'Monitoring', 'Retail'),
