INSERT INTO `cons_req` (`unique_nr`, `standard`, `type_req`, `req_nr`, `description`, `mandatory_l1`, `sec_class`, `prim_keyw`, `sec_keyw`, `cat_nr`, `iotsfmapetsi`, `threats`, `remarks`, `ref_author`, `ref_url`, `licence`, `level_2`, `level_3`) VALUES
(1, 'ETSI consumer security IoT', 'requirement', '5.1-1', 'All IoT device passwords shall be unique and shall not be resettable to any universal factory default value.', 'Yes', NULL, 'No universal default passwords', NULL, '5.1', NULL, NULL, 'The aim of the above provisions is to ensure that IoT services are kept up and running as the adoption of IoT devices across all aspects of a consumer\'s life increases, including in functions that are relevant to personal safety. The impact on people\'s lives could be prevalent if, for example, an internet connection is lost to a connected door and someone is locked outside. Another example is a home heating system that turns off because of a DDoS attack against a cloud service. It is important to note that other safety-related regulations can apply, but the key is to avoid making outages the cause of these problems and to design products and services ready for these challenges.', 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(2, 'ETSI consumer security IoT', 'requirement', '5.10-1', 'If telemetry data is collected from IoT devices and services, such as usage and measurement data, it should be examined for security anomalies.', 'Yes', NULL, 'Examine system telemetry data', NULL, '5.10', NULL, NULL, NULL, 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(3, 'ETSI consumer security IoT', 'requirement', '5.10-2', 'If telemetry data is collected from IoT devices and services, the processing of personal data should be kept to a minimum and such data should be anonymized.', 'Yes', NULL, 'Examine system telemetry data', NULL, '5.10', NULL, NULL, '<p>IoT systems and devices are relied upon by consumers for increasingly important use cases that can be safety-relevant or life-impacting. Keeping services running locally if there is a loss of network is one of the measures that can be taken to increase resilience. Other measures can include building redundancy into associated services as well as mitigations against, for example, Distributed Denial of Service (DDoS) attacks or signalling storms which can be caused by massreconnections of devices following an outage. It is expected that the level of resilience necessary is proportionate and determined by usage, with consideration given to others that rely on the system, service or device given that an outage can have a wider impact than expected.&nbsp;<br></p>', 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(4, 'ETSI consumer security IoT', 'requirement', '5.10-3', 'If telemetry data is collected from IoT devices and services, consumers shall be provided with information on what telemetry data is collected and the reasons for this.', 'No', NULL, 'Examine system telemetry data', NULL, '5.10', NULL, NULL, 'It is expected that the appropriate entity, such as the service provider or device manufacturer, ensures that personal data is processed in accordance with applicable data protection law, for example the GDPR [i.7], and also in accordance with applicable legislation regarding security and regulatory matters. Obtaining consent \"in a valid way\" normally involves giving consumers a free, obvious and explicit opt-in choice of whether their personal data can be used for a specified purpose. Consumers expect to be provided with means to preserve their privacy by means of configuring IoT device and service functionality appropriately.', 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(5, 'ETSI consumer security IoT', 'requirement', '5.11-1', 'Devices and services should be configured such that personal data can easily be removed from them when there is a transfer of ownership, if the consumer wishes to delete it, if the consumer wishes to remove a service from the device and/or at disposal of the device', 'No', NULL, 'Make it easy for consumers to delete personal data', NULL, '5.11', NULL, NULL, NULL, 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(6, 'ETSI consumer security IoT', 'requirement', '5.11-2', 'Consumers should be given clear instructions on how to delete their personal data.', 'No', NULL, 'Make it easy for consumers to delete personal data', NULL, '5.11', NULL, NULL, NULL, 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(7, 'ETSI consumer security IoT', 'requirement', '5.11-3', 'Consumers should be provided with clear confirmation that personal data has been deleted from services, devices and applications.', 'Yes', NULL, 'Make it easy for consumers to delete personal data', NULL, '5.11', NULL, NULL, 'The ability to remotely recover from these situations can rely on a known good state, such as locally storing a known good version to enable safe recovery and updating of the device. This will avoid denial of service and costly recalls or maintenance visits, whilst managing the risk of potential takeover of the device by an attacker subverting update or other network communications mechanisms. If an IoT device detects something unusual has happened with its software, it will be able to inform the right person. In some cases, devices can have the ability to be in administration mode - for example, there can be a user mode for a thermostat in a room that prevents other settings being changed. In these cases, an alert to the administrator is appropriate as that person has the ability to act on the alert.', 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(8, 'ETSI consumer security IoT', 'requirement', '5.12-1', 'Installation and maintenance of IoT devices should employ minimal steps and should follow security best practice on usability. Consumers should also be provided with guidance on how to securely set up their device.', 'Yes', NULL, 'Make installation and maintenance of devices easy', NULL, '5.12', NULL, NULL, NULL, 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(9, 'ETSI consumer security IoT', 'requirement', '5.13-1', 'Data input via user interfaces and transferred via application programming interfaces (APIs) or between networks in services and devices shall be validated.', 'Yes', NULL, 'Validate input data', NULL, '5.13', NULL, NULL, NULL, 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(10, 'ETSI consumer security IoT', 'requirement', '5.2-1', 'Companies that provide internet-connected devices and services shall provide a public point of contact as part of a vulnerability disclosure policy in order that security researchers and others are able to report issues.', 'Yes', NULL, 'Implement a means to manage reports of vulnerabilities', NULL, '5.2', NULL, NULL, NULL, 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(11, 'ETSI consumer security IoT', 'requirement', '5.2-2', 'Disclosed vulnerabilities should be acted on in a timely manner.', 'Yes', NULL, 'Implement a means to manage reports of vulnerabilities', NULL, '5.2', NULL, NULL, NULL, 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(12, 'ETSI consumer security IoT', 'requirement', '5.2-3', 'Companies should continually monitor for, identify and rectify security vulnerabilities within products and services they sell, produce, have produced and services they operate as part of the product security lifecycle.', 'Yes', NULL, 'Implement a means to manage reports of vulnerabilities', NULL, '5.2', NULL, NULL, NULL, 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(13, 'ETSI consumer security IoT', 'requirement', '5.3-1', 'All software components in consumer IoT devices should be securely updateable.', 'Yes', NULL, 'Keep software updated', NULL, '5.3', NULL, NULL, NULL, 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(14, 'ETSI consumer security IoT', 'requirement', '5.3-2', 'The consumer should be informed by the appropriate entity, such as the manufacturer or service provider, that an update is required.', 'Yes', NULL, 'Keep software updated', NULL, '5.3', NULL, NULL, 'It is expected that products meet the needs of users whilst remaining resilient to attacks on encryption. However,\nappropriateness of security controls and the use of encryption is dependent on many factors including the usage context.\nAs security is ever-evolving it is difficult to give prescriptive advice about encryption measures without the risk of such\nadvice quickly becoming obsolete.', 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(15, 'ETSI consumer security IoT', 'requirement', '5.3-3', 'When software components are updateable, updates shall be timely.', 'Yes', NULL, 'Keep software updated', NULL, '5.3', NULL, NULL, NULL, 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(16, 'ETSI consumer security IoT', 'requirement', '5.3-4', 'When software components are updateable, an end-of-life policy shall be published for devices that explicitly states the minimum length of time for which a device will receive software updates and the reasons for the length of the support period. this', 'Yes', NULL, 'Keep software updated', NULL, '5.3', NULL, NULL, 'Reverse engineering of devices and applications can easily discover credentials such as hard-coded usernames and passwords in software. Simple obfuscation methods also used to obscure or encrypt this hard-coded information can be trivially broken. Secure, trusted storage mechanisms can be used to secure security-sensitive data, such as those provided by a Trusted Execution Environment (TEE) and associated trusted, secure storage, or the secure storage and processing capabilities of software running on a Universal Integrated Circuit Card UICC/embedded Universal Integrated Circuit Card (eUICC).', 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(17, 'ETSI consumer security IoT', 'requirement', '5.3-5', 'When software components are updateable, the need for each update should be made clear to consumers and an update should be easy to implement.', 'No', NULL, 'Keep software updated', NULL, '5.3', NULL, NULL, 'There are some situations where devices cannot be patched. For constrained devices a replacement plan needs to be in place and be clearly communicated to the consumer. This plan would typically detail a schedule for when technologies will need to be replaced and, where applicable, when support for hardware and software ends.', 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(18, 'ETSI consumer security IoT', 'requirement', '5.3-6', 'When software components are updateable, updates should, where possible, maintain the basic functioning of the device, which can be critical to remain available during an update.', 'Yes', NULL, 'Keep software updated', NULL, '5.3', NULL, NULL, NULL, 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(19, 'ETSI consumer security IoT', 'requirement', '5.3-7', 'When software components are updateable, the provenance of software updates should be assured and security patches should be delivered over a secure channel.', 'Yes', NULL, 'Keep software updated', NULL, '5.3', NULL, NULL, 'NOTE 2: Software update mechanisms can present an attack vector.', 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(20, 'ETSI consumer security IoT', 'requirement', '5.3-8', 'For constrained devices that cannot have their software updated, the product should be isolable and the hardware replaceable.', 'Yes', NULL, 'Keep software updated', NULL, '5.3', NULL, NULL, '<p>It can be critical for consumers that a device continues to operate during an update. This is why the provision above recommends to \"maintain the basic functioning of the device\" where possible. </p><p>EXAMPLE: During an update, a watch is expected to continue to tell the time, a home thermostat is expected to continue to be operable and heating settings changeable by users and a smart lock usable for unlocking and locking a door. This can become a critical safety issue for some types of devices and systems if not considered or managed correctly.Particularly, devices that fulfil a safetyrelevant function are expected not to turn completely off in the case of an update; some minimal system functional capability is expected.</p>', 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(21, 'ETSI consumer security IoT', 'requirement', '5.3-9', 'For constrained devices that cannot have their software updated, the rationale for the absence of software updates, the period of hardware replacement support and an end-of-life policy should be published in an accessible way that is clear and transparent to the consumer', 'No', NULL, 'Keep software updated', NULL, '5.3', NULL, NULL, '<div>Developing and deploying software security updates in a timely manner is one of the most important actions a company</div><div>can take to protect its customers and the wider technical ecosystem. Vulnerabilities often stem from software</div><div>components that are not considered to be security related. It is good practice that all software is kept updated and well</div><div>maintained.</div><div><br></div><div>\"Timely\" in the context of software updates can vary, depending on the particular issue and fix, as well as other factors,</div><div>such as the ability to reach a device or constrained device considerations. For a non-critical bug, it can be acceptable to</div><div>deliver a software update within a regular patch cycle; for a severe, remote code execution flaw, a more immediate</div><div>update can be expected.</div><div><br></div><div>Software security updates can be provided for devices in a preventative manner, often as part of automatic updates,</div><div>which can remove security vulnerabilities before they are exploited. Managing this can be complex, especially if there</div><div>are parallel cloud service updates, device updates and other service updates to deal with. Therefore, a clear management</div><div>and deployment plan is essential, as is transparency to consumers about the current state of update support.</div><div><br></div><div>In many cases, publishing software updates involves multiple dependencies on other organizations such as</div><div>manufacturers of sub-components, however, this is not a reason to withhold updates. It is essential that the entire</div><div>software supply chain is considered in the development and deployment of security updates.</div><div><br></div><div>Software updates are expected to be provided after the sale of a device and pushed to devices for a period appropriate to</div><div>the device. When purchasing the product, the consumer expects this period of software update support to be made clear.</div><div><br></div><div>Software in consumer IoT devices can be one or more categories (this is a non-exhaustive list): firmware, platform</div><div>software such as an operating system, services and applications. The update process can be different for the different</div><div>categories of software.&nbsp;</div>', 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(22, 'ETSI consumer security IoT', 'requirement', '5.5-1', 'Security-sensitive data, including any remote management and control, should be encrypted in transit, with such encryption appropriate to the properties of the technology and usage.', 'No', NULL, 'Communicate securely', NULL, '5.5', NULL, NULL, '<p>&nbsp;This policy shall be published in an accessible way that is clear and transparent to the\nconsumer.<br></p>', 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(23, 'ETSI consumer security IoT', 'requirement', '5.5-2', 'All keys should be managed securely.', 'Yes', NULL, 'Communicate securely', NULL, '5.5', NULL, NULL, NULL, 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(24, 'ETSI consumer security IoT', 'requirement', '5.6-1', 'Unused software and network ports should be closed.', 'No', NULL, 'Minimize exposed attack surfaces', NULL, '5.6', NULL, NULL, '<p>NOTE 1: The appropriate entity is decided by the relevant jurisdiction.&nbsp;<br></p>', 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(25, 'ETSI consumer security IoT', 'requirement', '5.6-2', 'Hardware should not unnecessarily expose access to attack (e.g. open serial access, ports or test points).', 'Yes', NULL, 'Minimize exposed attack surfaces', NULL, '5.6', NULL, NULL, NULL, 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(26, 'ETSI consumer security IoT', 'requirement', '5.6-3', 'Software services should not be available if they are not used.', 'Yes', NULL, 'Minimize exposed attack surfaces', NULL, '5.6', NULL, NULL, '<p>Knowing about a security vulnerability allows companies to respond. Vulnerabilities are expected to be reported\ndirectly to the affected stakeholders in the first instance. If that is not possible vulnerabilities can be reported to national\nauthorities. Companies are also encouraged to share information with competent industry bodies.</p><p><b>NOTE 1</b>: Competent industry bodies include the GSMA and the IoT Security Foundation. Guidance on\nCoordinated Vulnerability Disclosure is available from the IoT Security Foundation which references the\nISO/IEC 29147 standard on vulnerability disclosure [i.4]. The GSMA\'s industry level Coordinated\nVulnerability Disclosure programme is located at: https://www.gsma.com/cvd.\n</p><p><b>Coordinated Vulnerability Disclosure (CVD)</b> is standardized by the International Organization for Standardization\n(ISO), is simple to implement and has been proven to be successful in some large software companies around the world\n[i.4]. CVD is, however, still not established in the IoT industry and some companies are reticent about dealing with\nsecurity researchers. CVD provides a way for security researchers to contact companies to inform them of security\nissues putting the company ahead of the threat of malicious exploitation and giving them an opportunity to resolve\nvulnerabilities in advance of a public disclosure.</p><p>\nCompanies that provide internet-connected devices and services have a duty of care to consumers and third parties who\ncan be harmed by their failure to have a CVD programme in place. Additionally, companies that share this information\nthrough industry bodies can assist others who can be suffering from the same problem.\n</p><p>Disclosures can comprise different approaches depending on the circumstances:\n</p><ol><li>• Vulnerabilities related to single products or services: the problem is expected to be reported directly to the\naffected stakeholder (e.g. device manufacturer, IoT service provider or mobile application developer). The\nsource of these reports can be security researchers or industry peers.\n</li><li>• Systemic vulnerabilities: a stakeholder, such as a device manufacturer, can discover a problem that is\npotentially systemic. Whilst fixing it in the device manufacturer\'s own product is crucial, there is significant\nbenefit to industry and consumers from sharing this information. Similarly, security researchers can also seek\nto report such systemic vulnerabilities. In this case, a relevant competent industry body can coordinate a wider\nscale response.</li></ol><p><b style=\"font-size: 0.875rem;\">NOTE 2:</b><span style=\"font-size: 0.875rem;\"> The Common Vulnerability Reporting Framework (CVRF) [i.5] can also be useful to exchange\ninformation on security vulnerabilities.</span></p><p><span style=\"font-size: 0.875rem;\">\nCyber security threat information sharing can support organizations in developing and producing secure products [i.6].&nbsp;</span></p>', 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(27, 'ETSI consumer security IoT', 'requirement', '5.6-4', 'Code should be minimized to the functionality necessary for the service/device to operate.', 'Yes', NULL, 'Minimize exposed attack surfaces', NULL, '5.6', NULL, NULL, 'A &#34;timely manner&#34; for acting on vulnerabilities varies considerably and is incident specific, however, the de facto\nstandard for the vulnerability process to be completed is within 90 days. A hardware fix can take considerably longer to\naddress than a software fix. Additionally, a fix that has to be deployed to devices can take time to roll out compared\nwith a server software fix.', 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(28, 'ETSI consumer security IoT', 'requirement', '5.6-5', 'Software should run with least necessary privileges, taking account of both security and functionality.', 'No', NULL, 'Minimize exposed attack surfaces', NULL, '5.6', NULL, NULL, 'no remarks', 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(29, 'ETSI consumer security IoT', 'requirement', '5.7-1', 'Software on IoT devices should be verified using secure boot mechanisms, which require a hardware root of trust.', 'Yes', NULL, 'Ensure software integrity', NULL, '5.7', NULL, NULL, 'Systems can be subverted by incorrectly formatted data or code transferred across different types of interface. Automated tools are often employed by attackers in order to exploit potential gaps and weaknesses that emerge as a result of not validating data. Examples include, but are not limited to, data that is: i) Not of the expected type, for example executable code rather than user inputted text. ii) Out of range, for example a temperature value which is beyond the limits of a sensor.', 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(30, 'ETSI consumer security IoT', 'requirement', '5.7-21', 'If an unauthorized change is detected to the software, the device should alert the consumer and/or administrator to an issue and should not connect to wider networks than those necessary to perform the alerting function.', 'No', NULL, 'Ensure software integrity', NULL, '5.7', NULL, NULL, 'Security issues caused by consumer confusion or misconfiguration can be reduced and sometimes eliminated by properly addressing complexity and poor design in user interfaces. Clear guidance to users on how to configure devices securely can also reduce their exposure to threats.', 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(31, 'ETSI consumer security IoT', 'requirement', '5.8-1', 'Device manufacturers and service providers shall provide consumers with clear and transparent information about how their personal data is being used, by whom, and for what purposes, for each device and service. This also applies to third parties that ca', 'No', NULL, NULL, NULL, '5.8', NULL, NULL, 'IoT devices often change ownership and will eventually be recycled or disposed of. Mechanisms can be provided that allow the consumer to remain in control and remove personal data from services, devices and applications. When a consumer wishes to completely remove their personal data, they also expect this to include backup copies that the service provider may hold. Deleting personal data from a device or service is not simply achieved by resetting a device back to its factory settings. There are many use cases where the consumer is not the owner of a device, but wishes to delete their own personal data from the device and all associated services such as cloud services or mobile applications. EXAMPLE: A user can have temporary usage of consumer IoT products within a rented apartment. Carrying out a factory reset of the product can remove configuration settings or disable the device to the detriment of the apartment owner and a future user. It would be an inappropriate technical mechanism to delete all personal data in this context.', 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(32, 'ETSI consumer security IoT', 'requirement', '5.8-21', 'Where personal data is processed on the basis of consumers\' consent, this consent shall be obtained in a valid way.', 'No', NULL, NULL, NULL, '5.8', NULL, NULL, NULL, 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(33, 'ETSI consumer security IoT', 'requirement', '5.8-3', 'Consumers who gave consent for the processing of their personal data shall be given the opportunity to withdraw it at any time.', 'No', NULL, NULL, NULL, '5.8', NULL, NULL, NULL, 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(34, 'ETSI consumer security IoT', 'requirement', '5.9-1', 'Resilience should be built in to IoT devices and services where required by their usage or by other relying systems, taking into account the possibility of outages of data networks and power.', 'Yes', NULL, 'Make systems resilient to outages', NULL, '5.9', NULL, NULL, 'Examining telemetry, including log data, is useful for security evaluation and allows for unusual circumstances to be identified early and dealt with, minimizing security risk and allowing quick mitigation of problems.', 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(35, 'ETSI consumer security IoT', 'requirement', '5.9-2', 'As far as reasonably possible, IoT services should remain operating and locally functional in the case of a loss of network and should recover cleanly in the case of restoration of a loss of power.', 'Yes', NULL, 'Make systems resilient to outages', NULL, '5.9', NULL, NULL, NULL, 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(36, 'ETSI consumer security IoT', 'requirement', '5.9-3', 'Devices should be able to return to a network in an expected, operational and stable state and in an orderly fashion, rather than in a massive-scale reconnect.', 'No', NULL, 'Make systems resilient to outages', NULL, '5.9', NULL, NULL, NULL, 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(37, 'ETSI consumer security IoT', 'requirement', '5.4-1', 'Credentials and security-sensitive data shall be stored securely within services and on devices. Hard-coded credentials in device software shall not be used.', 'Yes', NULL, 'Securely store credentials and security-sensitive data', NULL, NULL, NULL, NULL, 'Many IoT devices are being sold with universal default usernames and passwords (such as &#34;admin, admin&#34;) for user\ninterfaces through to network protocols. This has been the source of many security issues in IoT and the practice needs\nto be discontinued. Following best practice on passwords and other authentication methods is encouraged. Device\nsecurity can further be strengthened by having unique and immutable identities.\nFor guidance see, for example, the NIST Special Publication on Digital Identity Guidelines,\nAuthentication and Lifecycle Management [i.3].', 'ETSI TS 103 645 V1.1.1 (2019-02) Cyber Security for Consumer Internet of Things', 'https://www.etsi.org/deliver/etsi_en/303600_303699/303645/02.01.01_60/en_303645v020101p.pdf', 'No part may be reproduced or utilized in any form or by any means, electronic or mechanical, including photocopying and microfilm except as authorized by written permission of ETSI. © ETSI 2019. All rights reserved', NULL, NULL),
(38, 'ENISA Industry 4.0', 'requirement', 'GP-TM-47', 'If possible, limit the number of protocols implemented within a given environment to ensure manageability of the system. Also, disable all unused default network services.', NULL, NULL, 'III. Technical practices', 'Networks, protocols and encryption', NULL, NULL, 'Eavesdropping / Interception / Hijacking\nPhysical attacks\nOutages', 'Industrial Security: Applying IoT Security Controls on the Industrial Plant Floor\nSecurity Guidance for Early Adopters of the Internet of Things\nInternet of Things (IoT) Security and Privacy Recommendations\nNIST SP 800 53r4: Security and Privacy Controls for Federal Information Systems and Organizations\nIEC 62443-3-3:2013 System security requirements and security levels\nThe 2018 SANS Industrial IoT Security Survey: Shaping IIoT Security Concerns', 'Siemens\nCloud Security Alliance\nBITAG (Broadband Internet Technical Advisory Group)\nNIST\nIEC\nSANS Institute', 'https://www.industry.usa.siemens.com/automation/us/en/formsdocs/Documents/2016%20MIA-%2023%20Industrial%20Security%20Applying%20IoT%20Security%20Controls%20on%20the%20Industrial%20Plant%20Floor.pdf\nhttps://downloads.cloudsecurityalliance.org/whitepapers/Security_Guidance_for_Early_Adopters_of_the_Internet_of_Things.pdf\nhttps://www.bitag.org/documents/BITAG_Report_-_Internet_of_Things_(IoT)_Security_and_Privacy_Recommendations.pdf\nhttps://nvlpubs.nist.gov/nistpubs/SpecialPublications/NIST.SP.800-53r4.pdf\nhttps://webstore.iec.ch/publication/7033\nhttps://www.sans.org/reading-room/whitepapers/analyst/2018-industrial-iot-security-survey-shaping-iiot-security-concerns-38505', NULL, NULL, NULL),
(39, 'ENISA Industry 4.0', 'requirement', 'GP-OP-10', 'Define cyber incidents relevant for your organisation based on the company\'s area and range of operation. Classify these incidents according to applicable standards, e.g. by grouping them based on utilisation of a common attack vector (removable media, email, website, etc.) or according to their impact (on organisation\'s operation, on data, etc.).', NULL, NULL, 'II. Organizational practices', 'Incidents handling', NULL, NULL, 'Nefarious Activity / Abuse\nEavesdropping / Interception / Hijacking\nPhysical attacks\nUnintentional damages (accidental)\nFailures / Malfunctions\nOutages\nLegal\nDisasters', 'IEC 62443-2-1:2010 Establishing an industrial automation and control system security program\nIEC 62443-3-3:2013 System security requirements and security levels\nNIST SP 800-61r2: Computer Security Incident Handling Guide - Recommendations of the National Institute of Standards and Technology\nNIST SP 800 82r2: Guide to Industrial Control Systems (ICS) Security\nSmarter Security for Manufacturing in The Industry 4.0 Era: Industry 4.0 Cyber Resilience for the Manufacturing of the Future\nNISTIR 8183: Cybersecurity Framework Manufacturing Profile\nIdentity and Access Management for the Internet of Things - Summary Guidance\nInternet of Things Security Guidelines v1.2\nSecurity capabilities supporting safety of the Internet of things\nNIST SP 800 53r4: Security and Privacy Controls for Federal Information Systems and Organizations\nIoT Security Maturity Model: Description and Intended Use', 'IEC\nIEC\nNIST\nNIST\nSymantec\nNIST\nCloud Security Alliance\nIoT Alliance Australia\nInternational Telecommunications Union\nNIST\nIIC (Industrial Internet Consortium)', 'https://webstore.iec.ch/publication/7030\nhttps://webstore.iec.ch/publication/7033\nhttps://nvlpubs.nist.gov/nistpubs/specialpublications/nist.sp.800-61r2.pdf\nhttps://nvlpubs.nist.gov/nistpubs/SpecialPublications/NIST.SP.800-82r2.pdf\nhttps://www.symantec.com/content/dam/symantec/docs/solution-briefs/industry-4.0-en.pdf\nhttps://nvlpubs.nist.gov/nistpubs/ir/2017/NIST.IR.8183.pdf\nhttps://downloads.cloudsecurityalliance.org/assets/research/internet-of-things/identity-and-access-management-for-the-iot.pdf\nhttp://www.iot.org.au/wp/wp-content/uploads/2016/12/IoTAA-Security-Guideline-V1.2.pdf\nhttps://www.itu.int/rec/T-REC-Y.4806/en\nhttps://nvlpubs.nist.gov/nistpubs/SpecialPublications/NIST.SP.800-53r4.pdf\nhttps://www.iiconsortium.org/pdf/SMM_Description_and_Intended_Use_2018-04-09.pdf', NULL, NULL, NULL),
(40, 'ENISA Industry 4.0', 'requirement', 'GP-TM-39', 'In case of extensive and diversified networks with a large number of devices, adopt the Privilege Access Management (PAM) solution to manage elevated privileges (i.e. administrator privileges) in an orderly manner.', NULL, NULL, 'III. Technical practices', 'Access Control', NULL, NULL, 'Nefarious Activity / Abuse\nEavesdropping / Interception / Hijacking\nPhysical attacks\nUnintentional damages (accidental)', 'Identity and Access Management for the Internet of Things - Summary Guidance\nSecurity Guidance for Early Adopters of the Internet of Things\nNIST SP 800 53r4: Security and Privacy Controls for Federal Information Systems and Organizations\nISO/IEC 27002:2013 Information technology -- Security techniques -- Code of practice for information security controls\nIEC 62443-2-1:2010 Establishing an industrial automation and control system security program', 'Cloud Security Alliance\nCloud Security Alliance\nNIST\nISO\nIEC', 'https://downloads.cloudsecurityalliance.org/assets/research/internet-of-things/identity-and-access-management-for-the-iot.pdf\nhttps://downloads.cloudsecurityalliance.org/whitepapers/Security_Guidance_for_Early_Adopters_of_the_Internet_of_Things.pdf\nhttps://nvlpubs.nist.gov/nistpubs/SpecialPublications/NIST.SP.800-53r4.pdf\nhttps://www.iso.org/standard/54533.html\nhttps://webstore.iec.ch/publication/7030', NULL, NULL, NULL),
(41, 'ENISA Industry 4.0', 'requirement', 'GP-OP-05', 'During the handover phase of the project implementation process, properly build and transfer all cybersecurity documentation, processes and procedures. Within the documentation, include a list of system and service accounts, security logs, response plans, confirmation of all software and firmware versions, up-to-date network diagrams, system architecture, risk register and security limitations. Processes should comprise maintenance routines, anti-virus deployment and assurance, patching processes and accounts\' management and authentication processes. Procedures should include firewalls baseline configurations, management and monitoring, change control and fall-over testing.', NULL, NULL, 'II. Organizational practices', 'Endpoints lifecycle', NULL, NULL, 'Nefarious Activity / Abuse\nEavesdropping / Interception / Hijacking\nFailures / Malfunctions\nLegal', 'NIST SP 800-52 r1: Guidelines for the Selection, Configuration, and Use of Transport Layer Security (TLS) Implementations\nCybersecurity for Smart Manufacturing\nIndustrial Internet of Things Volume G4: Security Framework\nISO/IEC 27001:2013 Information technology -- Security techniques -- Information security management systems -- Requirements\nNIST SP 800 53r4: Security and Privacy Controls for Federal Information Systems and Organizations\nNIST SP 800 82r2: Guide to Industrial Control Systems (ICS) Security', 'NIST\nNIST\nIIC (Industrial Internet Consortium)\nISO\nNIST\nNIST', 'https://nvlpubs.nist.gov/nistpubs/SpecialPublications/NIST.SP.800-52r1.pdf\nhttps://www.nist.gov/sites/default/files/documents/2016/12/05/cybersecurity_for_smart_manufacturing.pdf\nhttps://www.iiconsortium.org/pdf/IIC_PUB_G4_V1.00_PB.pdf\nhttps://www.iso.org/standard/54534.html\nhttps://nvlpubs.nist.gov/nistpubs/SpecialPublications/NIST.SP.800-53r4.pdf\nhttps://nvlpubs.nist.gov/nistpubs/SpecialPublications/NIST.SP.800-82r2.pdf', NULL, NULL, NULL),
(42, 'ENISA Industry 4.0', 'requirement', 'GP-TM-31', 'For control systems which cannot be updated (e.g. legacy systems), apply compensating measures, such as network segmentation, micro segmentation, system relocation or additional real-time monitoring tools.', NULL, NULL, 'III. Technical practices', 'Software/Firmware updates', NULL, NULL, 'Nefarious Activity / Abuse\nEavesdropping / Interception / Hijacking\nPhysical attacks\nUnintentional damages (accidental)\nFailures / Malfunctions\nOutages\nLegal\nDisasters', 'Connected Consumer Products. Best Practice Guidelines\nFramework for Improving Critical Infrastructure Cybersecurity V1.1\nNIST SP 800-52 r1: Guidelines for the Selection, Configuration, and Use of Transport Layer Security (TLS) Implementations\nIIC Endpoint Security Best Practices\nIndustrial Internet of Things Volume G4: Security Framework\nGSMA CLP.13 IoT Security Guidelines for Endpoint Ecosystems', 'IoT Security Foundation\nNIST\nNIST\nIIC (Industrial Internet Consortium)\nIIC (Industrial Internet Consortium)\nGSMA (Global System for Mobile Communications)', 'https://iotsecurityfoundation.org/wp-content/uploads/2016/12/Connected-Consumer-Products.pdf\nhttps://nvlpubs.nist.gov/nistpubs/CSWP/NIST.CSWP.04162018.pdf\nhttps://nvlpubs.nist.gov/nistpubs/SpecialPublications/NIST.SP.800-52r1.pdf\nhttps://www.iiconsortium.org/pdf/Endpoint_Security_Best_Practices_Final_Mar_2018.pdf\nhttps://www.iiconsortium.org/pdf/IIC_PUB_G4_V1.00_PB.pdf\nhttps://www.gsma.com/iot/wp-content/uploads/2016/02/CLP.13-v1.0.pdf', NULL, NULL, NULL);
INSERT INTO `cons_req` (`unique_nr`, `standard`, `type_req`, `req_nr`, `description`, `mandatory_l1`, `sec_class`, `prim_keyw`, `sec_keyw`, `cat_nr`, `iotsfmapetsi`, `threats`, `remarks`, `ref_author`, `ref_url`, `licence`, `level_2`, `level_3`) VALUES
(43, 'ENISA Industry 4.0', 'requirement', 'GP-TM-41', 'Ensure security of communications channels related to IIoT solutions. Encrypt communications in case of important data (e.g. configuration, personal data, data for control purposes), where it is possible to do so without affecting safety, availability and performance.', NULL, NULL, 'III. Technical practices', 'Networks, protocols and encryption', NULL, NULL, 'Eavesdropping / Interception / Hijacking', 'IoT Security Guidance\nInternet of Things (IoT) Security and Privacy Recommendations\nSecurity Guidance for Early Adopters of the Internet of Things\nGSMA CLP.14 IoT Security Guidelines for Network Operators\nNIST SP 800 82r2: Guide to Industrial Control Systems (ICS) Security\nThe Malicious Use of Artificial Intelligence: Forecasting, Prevention, and Mitigation\nInternet of Things Security Guidelines v1.2\nSecurity capabilities supporting safety of the Internet of things\nConnected Consumer Products. Best Practice Guidelines\nIndustrial Internet of Things Volume G4: Security Framework\nDraft NISTIR 8228: Considerations for Managing Internet of Things (IoT) Cybersecurity and Privacy Risks\nGSMA CLP.12 IoT Security Guidelines for IoT Service Ecosystems\nGSMA CLP.13 IoT Security Guidelines for Endpoint Ecosystems', 'OWASP (Open Web Application Security Project)\nBITAG (Broadband Internet Technical Advisory Group)\nCloud Security Alliance\nGSMA (Global System for Mobile Communications)\nNIST\nOpenAI and others\nIoT Alliance Australia\nInternational Telecommunications Union\nIoT Security Foundation\nIIC (Industrial Internet Consortium)\nNIST\nGSMA (Global System for Mobile Communications)\nGSMA (Global System for Mobile Communications)', 'https://www.owasp.org/index.php/IoT_Security_Guidance\nhttps://www.bitag.org/documents/BITAG_Report_-_Internet_of_Things_(IoT)_Security_and_Privacy_Recommendations.pdf\nhttps://downloads.cloudsecurityalliance.org/whitepapers/Security_Guidance_for_Early_Adopters_of_the_Internet_of_Things.pdf\nhttps://www.gsma.com/iot/wp-content/uploads/2017/10/CLP.14-v2.0.pdf\nhttps://nvlpubs.nist.gov/nistpubs/SpecialPublications/NIST.SP.800-82r2.pdf\nhttps://www.eff.org/files/2018/02/20/malicious_ai_report_final.pdf\nhttp://www.iot.org.au/wp/wp-content/uploads/2016/12/IoTAA-Security-Guideline-V1.2.pdf\nhttps://www.itu.int/rec/T-REC-Y.4806/en\nhttps://iotsecurityfoundation.org/wp-content/uploads/2016/12/Connected-Consumer-Products.pdf\nhttps://www.iiconsortium.org/pdf/IIC_PUB_G4_V1.00_PB.pdf\nhttps://nvlpubs.nist.gov/nistpubs/ir/2018/NIST.IR.8228-draft.pdf\nhttps://www.gsma.com/iot/wp-content/uploads/2016/02/CLP.12-v1.0.pdf\nhttps://www.gsma.com/iot/wp-content/uploads/2016/02/CLP.13-v1.0.pdf', NULL, NULL, NULL),
(44, 'ENISA Industry 4.0', 'requirement', 'GP-TM-05', 'Ensure production data integrity through utilisation of appropriate cryptographic mechanisms and key storage tailored to processing capabilities of the implemented solutions.', NULL, NULL, 'III. Technical practices', 'Trust and Integrity Management', NULL, NULL, 'Nefarious Activity / Abuse\nEavesdropping / Interception / Hijacking', 'Smarter Security for Manufacturing in The Industry 4.0 Era: Industry 4.0 Cyber Resilience for the Manufacturing of the Future\nIEC 62443-3-3:2013 System security requirements and security levels\nIoT Security White Paper 2017\nNISTIR 8183: Cybersecurity Framework Manufacturing Profile\nNIST SP 800 53r4: Security and Privacy Controls for Federal Information Systems and Organizations\nNIST SP 800 82r2: Guide to Industrial Control Systems (ICS) Security\nGSMA CLP.12 IoT Security Guidelines for IoT Service Ecosystems\nGSMA CLP.13 IoT Security Guidelines for Endpoint Ecosystems', 'Symantec\nIEC\nHuawei\nNIST\nNIST\nNIST\nGSMA (Global System for Mobile Communications)\nGSMA (Global System for Mobile Communications)', 'https://www.symantec.com/content/dam/symantec/docs/solution-briefs/industry-4.0-en.pdf\nhttps://webstore.iec.ch/publication/7033\nhttps://www.huawei.com/minisite/iot/img/hw_iot_secutity_white_paper_2017_en_v2.pdf\nhttps://nvlpubs.nist.gov/nistpubs/ir/2017/NIST.IR.8183.pdf\nhttps://nvlpubs.nist.gov/nistpubs/SpecialPublications/NIST.SP.800-53r4.pdf\nhttps://nvlpubs.nist.gov/nistpubs/SpecialPublications/NIST.SP.800-82r2.pdf\nhttps://www.gsma.com/iot/wp-content/uploads/2016/02/CLP.12-v1.0.pdf\nhttps://www.gsma.com/iot/wp-content/uploads/2016/02/CLP.13-v1.0.pdf', NULL, NULL, NULL),
(45, 'ENISA Industry 4.0', 'requirement', 'GP-TM-37', 'Avoid using shared accounts for access to IIoT devices and systems. Create individual accounts for every user whenever possible, as this will enable tracking of the performed actions to a specific person. If shared accounts are used, change passwords periodically (e.g. every 90 days) and in case of personnel changes within a shared account group (e.g. when an employee leaves). Also, consider deployment of additional compensating controls (e.g. segregation of duties, real-time monitoring tools such as industrial IDS).', NULL, NULL, 'III. Technical practices', 'Access Control', NULL, NULL, 'Physical attacks\nUnintentional damages (accidental)', 'Industrie 4.0 Security Guidelines Recommendations for actions\nISO/IEC 27001:2013 Information technology -- Security techniques -- Information security management systems -- Requirements\nISO/IEC 27002:2013 Information technology -- Security techniques -- Code of practice for information security controls\nIEC 62443-3-3:2013 System security requirements and security levels\nIEC 62443-2-1:2010 Establishing an industrial automation and control system security program\nGSMA CLP.12 IoT Security Guidelines for IoT Service Ecosystems\nGSMA CLP.13 IoT Security Guidelines for Endpoint Ecosystems', 'VDMA\nISO\nISO\nIEC\nIEC\nGSMA (Global System for Mobile Communications)\nGSMA (Global System for Mobile Communications)', 'http://www.vdmashop.de/refs/Leitf_I40_Security_En_LR_neu.pdf\nhttps://www.iso.org/standard/54534.html\nhttps://www.iso.org/standard/54533.html\nhttps://webstore.iec.ch/publication/7033\nhttps://webstore.iec.ch/publication/7030\nhttps://www.gsma.com/iot/wp-content/uploads/2016/02/CLP.12-v1.0.pdf\nhttps://www.gsma.com/iot/wp-content/uploads/2016/02/CLP.13-v1.0.pdf', NULL, NULL, NULL),
(46, 'ENISA Industry 4.0', 'requirement', 'GP-OP-27', 'Clearly define all relevant aspects of the partnership with Third Parties, including security, within the appropriate agreements and contracts (e.g. SLA - service level agreement, NDA - Non-Disclosure Agreements). Sign these agreements and contracts before the start of cooperation.', NULL, NULL, 'II. Organizational practices', 'Third Party Management', NULL, NULL, 'Unintentional damages (accidental)\nFailures / Malfunctions\nLegal', 'INDUSTRY 4.0 AND ICS SECTOR REPORT Cyber security for the industry 4.0 and ICS sector\nAutomotive Cybersecurity Best Practices - Executive Summary\nIEEE Std 802.1X-2010 - Port-Based Network Access Control\nNIST SP 800 82r2: Guide to Industrial Control Systems (ICS) Security\nSecurity Guidance for Early Adopters of the Internet of Things\nIndustrial Internet of Things Volume G4: Security Framework\nISO/IEC 27001:2013 Information technology -- Security techniques -- Information security management systems -- Requirements\nNIST SP 800 53r4: Security and Privacy Controls for Federal Information Systems and Organizations\nISO/IEC 27002:2013 Information technology -- Security techniques -- Code of practice for information security controls\nIEC 62443-2-1:2010 Establishing an industrial automation and control system security program\nDraft NISTIR 8228: Considerations for Managing Internet of Things (IoT) Cybersecurity and Privacy Risks', 'ECSO (European Cyber Security Organization)\nAuto ISAC (Automotive Information Sharing and Analysis Center)\nIEEE\nNIST\nCloud Security Alliance\nIIC (Industrial Internet Consortium)\nISO\nNIST\nISO\nIEC\nNIST', 'http://ecs-org.eu/documents/publications/5ad7268b92497.pdf\nhttp://www.sovereign-plc.co.uk/sites/default/files/Auto%20ISAC%20Cyber%20Security%20Best%20Practices%20Executive%20Summary.pdf\nhttp://moodle.eece.cu.edu.eg/pluginfile.php/1799/mod_folder/content/1/802.1X-2010.pdf?forcedownload=1\nhttps://nvlpubs.nist.gov/nistpubs/SpecialPublications/NIST.SP.800-82r2.pdf\nhttps://downloads.cloudsecurityalliance.org/whitepapers/Security_Guidance_for_Early_Adopters_of_the_Internet_of_Things.pdf\nhttps://www.iiconsortium.org/pdf/IIC_PUB_G4_V1.00_PB.pdf\nhttps://www.iso.org/standard/54534.html\nhttps://nvlpubs.nist.gov/nistpubs/SpecialPublications/NIST.SP.800-53r4.pdf\nhttps://www.iso.org/standard/54533.html\nhttps://webstore.iec.ch/publication/7030\nhttps://nvlpubs.nist.gov/nistpubs/ir/2018/NIST.IR.8228-draft.pdf', NULL, NULL, NULL),
(47, 'ENISA Industry 4.0', 'requirement', 'GP-TM-29', 'Perform deployment of patches for the IIoT devices only after proving that no negative consequences exist. Test the patches in a test environment before implementing them in production. If this is not possible, begin with deploying patches only on a segment of a system, ensuring that other zones will continue to operate normally in case a patch exerts any negative impact on a chosen segment.', NULL, NULL, 'III. Technical practices', 'Software/Firmware updates', NULL, NULL, 'Failures / Malfunctions', 'Strategic Principles for Securing the Internet of Things\nConnected Consumer Products. Best Practice Guidelines\nHippocratic Oath for Connected Medical Devices\nNIST SP 800 82r2: Guide to Industrial Control Systems (ICS) Security\nSecurity Guidance for Early Adopters of the Internet of Things\nIndustrial Internet of Things Volume G4: Security Framework\nNIST SP 800 53r4: Security and Privacy Controls for Federal Information Systems and Organizations\nISO/IEC 27002:2013 Information technology -- Security techniques -- Code of practice for information security controls\nIoT Security Maturity Model: Description and Intended Use', 'Homeland Security\nIoT Security Foundation\nThe Cavalry\nNIST\nCloud Security Alliance\nIIC (Industrial Internet Consortium)\nNIST\nISO\nIIC (Industrial Internet Consortium)', 'https://www.dhs.gov/sites/default/files/publications/Strategic_Principles_for_Securing_the_Internet_of_Things-2016-1115-FINAL....pdf\nhttps://iotsecurityfoundation.org/wp-content/uploads/2016/12/Connected-Consumer-Products.pdf\nhttps://www.iamthecavalry.org/wp-content/uploads/2016/01/I-Am-The-Cavalry-Hippocratic-Oath-for-Connected-Medical-Devices.pdf\nhttps://nvlpubs.nist.gov/nistpubs/SpecialPublications/NIST.SP.800-82r2.pdf\nhttps://downloads.cloudsecurityalliance.org/whitepapers/Security_Guidance_for_Early_Adopters_of_the_Internet_of_Things.pdf\nhttps://www.iiconsortium.org/pdf/IIC_PUB_G4_V1.00_PB.pdf\nhttps://nvlpubs.nist.gov/nistpubs/SpecialPublications/NIST.SP.800-53r4.pdf\nhttps://www.iso.org/standard/54533.html\nhttps://www.iiconsortium.org/pdf/SMM_Description_and_Intended_Use_2018-04-09.pdf', NULL, NULL, NULL),
(48, 'ENISA Industry 4.0', 'requirement', 'GP-TM-33', 'Ensure minimal level of authentication security for the IIoT devices and systems. In a segmented network/system, ensure that authorisation only allows for access to a certain segment and no other parts of the system.', NULL, NULL, 'III. Technical practices', 'Access Control', NULL, NULL, 'Nefarious Activity / Abuse\nEavesdropping / Interception / Hijacking\nUnintentional damages (accidental)', 'IoT Security Guidance\nIdentity and Access Management for the Internet of Things - Summary Guidance\nSecurity Guidance for Early Adopters of the Internet of Things\nInternet of Things Security Guidelines v1.2\nConnected Consumer Products. Best Practice Guidelines\nIndustrial Internet of Things Volume G4: Security Framework\nIndustry 4.0: Secure by design\nISO/IEC 27002:2013 Information technology -- Security techniques -- Code of practice for information security controls\nIEC 62443-3-3:2013 System security requirements and security levels\nIoT Security White Paper 2017\nGSMA CLP.12 IoT Security Guidelines for IoT Service Ecosystems\nGSMA CLP.13 IoT Security Guidelines for Endpoint Ecosystems\nIoT Security Maturity Model: Description and Intended Use', 'OWASP (Open Web Application Security Project)\nCloud Security Alliance\nCloud Security Alliance\nIoT Alliance Australia\nIoT Security Foundation\nIIC (Industrial Internet Consortium)\nVDC\nISO\nIEC\nHuawei\nGSMA (Global System for Mobile Communications)\nGSMA (Global System for Mobile Communications)\nIIC (Industrial Internet Consortium)', 'https://www.owasp.org/index.php/IoT_Security_Guidance\nhttps://downloads.cloudsecurityalliance.org/assets/research/internet-of-things/identity-and-access-management-for-the-iot.pdf\nhttps://downloads.cloudsecurityalliance.org/whitepapers/Security_Guidance_for_Early_Adopters_of_the_Internet_of_Things.pdf\nhttp://www.iot.org.au/wp/wp-content/uploads/2016/12/IoTAA-Security-Guideline-V1.2.pdf\nhttps://iotsecurityfoundation.org/wp-content/uploads/2016/12/Connected-Consumer-Products.pdf\nhttps://www.iiconsortium.org/pdf/IIC_PUB_G4_V1.00_PB.pdf\nhttps://cdn2.hubspot.net/hubfs/582328/whitepapers/VDC%20-%20Industry%204.0%20Secure%20by%20Design%20-%20for%20GrammaTech.pdf?t=1519834251604\nhttps://www.iso.org/standard/54533.html\nhttps://webstore.iec.ch/publication/7033\nhttps://www.huawei.com/minisite/iot/img/hw_iot_secutity_white_paper_2017_en_v2.pdf\nhttps://www.gsma.com/iot/wp-content/uploads/2016/02/CLP.12-v1.0.pdf\nhttps://www.gsma.com/iot/wp-content/uploads/2016/02/CLP.13-v1.0.pdf\nhttps://www.iiconsortium.org/pdf/SMM_Description_and_Intended_Use_2018-04-09.pdf', NULL, NULL, NULL),
(49, 'ENISA Industry 4.0', 'requirement', 'GP-PS-17', 'Avoid the usage of removable devices and disable USB ports (or technically restrict use of removable media on USB ports) if there is no accepted business requirement. At least scan the removable media devices using malware detection software with up-to-date definitions if they need to be connected to the environment.', NULL, NULL, 'I. Policies', 'Asset Management', NULL, NULL, 'Nefarious Activity / Abuse\nEavesdropping / Interception / Hijacking\nPhysical attacks\nUnintentional damages (accidental)', 'NIST SP 800 53r4: Security and Privacy Controls for Federal Information Systems and Organizations\nIndustrial Internet of Things Volume G4: Security Framework\nNISTIR 8183: Cybersecurity Framework Manufacturing Profile\nIndustrial Security: Applying IoT Security Controls on the Industrial Plant Floor\nNIST SP 800 82r2: Guide to Industrial Control Systems (ICS) Security\nPutting Industrial Cyber Security at the top of the CEO agenda\nISO/IEC 27002:2013 Information technology -- Security techniques -- Code of practice for information security controls\nIEC 62443-2-1:2010 Establishing an industrial automation and control system security program\nIEC 62443-3-3:2013 System security requirements and security levels\nGSMA CLP.13 IoT Security Guidelines for Endpoint Ecosystems\nDraft NISTIR 8228: Considerations for Managing Internet of Things (IoT) Cybersecurity and Privacy Risks', 'NIST\nIIC (Industrial Internet Consortium)\nNIST\nSiemens\nNIST\nLNS\nISO\nIEC\nIEC\nGSMA (Global System for Mobile Communications)\nNIST', 'https://nvlpubs.nist.gov/nistpubs/SpecialPublications/NIST.SP.800-53r4.pdf\nhttps://www.iiconsortium.org/pdf/IIC_PUB_G4_V1.00_PB.pdf\nhttps://nvlpubs.nist.gov/nistpubs/ir/2017/NIST.IR.8183.pdf\nhttps://www.industry.usa.siemens.com/automation/us/en/formsdocs/Documents/2016%20MIA-%2023%20Industrial%20Security%20Applying%20IoT%20Security%20Controls%20on%20the%20Industrial%20Plant%20Floor.pdf\nhttps://nvlpubs.nist.gov/nistpubs/SpecialPublications/NIST.SP.800-82r2.pdf\nhttps://www.honeywellprocess.com/en-US/online_campaigns/lns-cyber-report/Pages/Honeywell-LNS-Study_PuttingIndustrialCyberSecurityattheTopCEOAgenda.pdf\nhttps://www.iso.org/standard/54533.html\nhttps://webstore.iec.ch/publication/7030\nhttps://webstore.iec.ch/publication/7033\nhttps://www.gsma.com/iot/wp-content/uploads/2016/02/CLP.13-v1.0.pdf\nhttps://nvlpubs.nist.gov/nistpubs/ir/2018/NIST.IR.8228-draft.pdf', NULL, NULL, NULL),
(50, 'ENISA Industry 4.0', 'requirement', 'GP-PS-13', 'In complex industrial environments with legacy system asset discovery, use passive monitoring devices instead of active monitoring solutions. It is advisable to utilise passive automatic tools whenever it is feasible, as they do not disrupt systems operation. Utilisation of active monitoring devices can cause adverse effects on the OT environment and disrupt the production process.', NULL, NULL, 'I. Policies', 'Asset Management', NULL, NULL, 'Nefarious Activity / Abuse\nEavesdropping / Interception / Hijacking', 'Industrial Internet of Things Volume G4: Security Framework\nSecurity Guidance for Early Adopters of the Internet of Things\nIEC 62443-3-3:2013 System security requirements and security levels\nIoT Security White Paper 2017\nIEC 62443-2-1:2010 Establishing an industrial automation and control system security program\nNIST SP 800 82r2: Guide to Industrial Control Systems (ICS) Security', 'IIC (Industrial Internet Consortium)\nCloud Security Alliance\nIEC\nHuawei\nIEC\nNIST', 'https://www.iiconsortium.org/pdf/IIC_PUB_G4_V1.00_PB.pdf\nhttps://downloads.cloudsecurityalliance.org/whitepapers/Security_Guidance_for_Early_Adopters_of_the_Internet_of_Things.pdf\nhttps://webstore.iec.ch/publication/7033\nhttps://www.huawei.com/minisite/iot/img/hw_iot_secutity_white_paper_2017_en_v2.pdf\nhttps://webstore.iec.ch/publication/7030\nhttps://nvlpubs.nist.gov/nistpubs/SpecialPublications/NIST.SP.800-82r2.pdf', NULL, NULL, NULL),
(51, 'ENISA Industry 4.0', 'requirement', 'GP-OP-22', 'Train users of IIoT on the secure usage of their devices. During training sessions, explain to the IIoT users all of the technologies deployed to protect IIoT devices and the ecosystem where the solution is deployed.', NULL, NULL, 'II. Organizational practices', 'Training and Awareness', NULL, NULL, 'Nefarious Activity / Abuse\nUnintentional damages (accidental)', 'Identity and Access Management for the Internet of Things - Summary Guidance\nNISTIR 8183: Cybersecurity Framework Manufacturing Profile\nNIST SP 800 82r2: Guide to Industrial Control Systems (ICS) Security\nSecurity Guidance for Early Adopters of the Internet of Things\nISO/IEC 27002:2013 Information technology -- Security techniques -- Code of practice for information security controls\nIEC 62443-2-1:2010 Establishing an industrial automation and control system security program', 'Cloud Security Alliance\nNIST\nNIST\nCloud Security Alliance\nISO\nIEC', 'https://downloads.cloudsecurityalliance.org/assets/research/internet-of-things/identity-and-access-management-for-the-iot.pdf\nhttps://nvlpubs.nist.gov/nistpubs/ir/2017/NIST.IR.8183.pdf\nhttps://nvlpubs.nist.gov/nistpubs/SpecialPublications/NIST.SP.800-82r2.pdf\nhttps://downloads.cloudsecurityalliance.org/whitepapers/Security_Guidance_for_Early_Adopters_of_the_Internet_of_Things.pdf\nhttps://www.iso.org/standard/54533.html\nhttps://webstore.iec.ch/publication/7030', NULL, NULL, NULL),
(52, 'ENISA Industry 4.0', 'requirement', 'GP-TM-44', 'Isolate safety networks from business and control networks. If this is not possible due to business requirements, ensure that solutions for network traffic filtering are in place.', NULL, NULL, 'III. Technical practices', 'Networks, protocols and encryption', NULL, NULL, 'Nefarious Activity / Abuse\nEavesdropping / Interception / Hijacking', 'Recommended Practice: Improving Industrial Control System Cybersecurity with Defense-in-Depth Strategies\nIEC 62443-1-1:2009 Terminology, concepts and models\nIEC 62443-2-1:2010 Establishing an industrial automation and control system security program\nIEC 62443-3-3:2013 System security requirements and security levels\nIndustrial Internet of Things Volume G4: Security Framework\nSecurity capabilities supporting safety of the Internet of things\nNIST SP 800 82r2: Guide to Industrial Control Systems (ICS) Security\nDraft NISTIR 8228: Considerations for Managing Internet of Things (IoT) Cybersecurity and Privacy Risks', 'Homeland Security\nIEC\nIEC\nIEC\nIIC (Industrial Internet Consortium)\nInternational Telecommunications Union\nNIST\nNIST', 'https://ics-cert.us-cert.gov/sites/default/files/recommended_practices/NCCIC_ICS-CERT_Defense_in_Depth_2016_S508C.pdf\nhttps://webstore.iec.ch/publication/7029\nhttps://webstore.iec.ch/publication/7030\nhttps://webstore.iec.ch/publication/7033\nhttps://www.iiconsortium.org/pdf/IIC_PUB_G4_V1.00_PB.pdf\nhttps://www.itu.int/rec/T-REC-Y.4806/en\nhttps://nvlpubs.nist.gov/nistpubs/SpecialPublications/NIST.SP.800-82r2.pdf\nhttps://nvlpubs.nist.gov/nistpubs/ir/2018/NIST.IR.8228-draft.pdf', NULL, NULL, NULL),
(53, 'ENISA Industry 4.0', 'requirement', 'GP-TM-52', 'Perform periodic reviews of access control privileges and asset configurations at least annually and in case of a major change in the system.', NULL, NULL, 'III. Technical practices', 'Monitoring and auditing', NULL, NULL, 'Nefarious Activity / Abuse\nEavesdropping / Interception / Hijacking\nPhysical attacks\nUnintentional damages (accidental)\nFailures / Malfunctions', 'NISTIR 8183: Cybersecurity Framework Manufacturing Profile\nIoT Security Guidance\nNISTIR 8200: Interagency Report on Status of International Cybersecurity Standardization for the Internet of Things (IoT)\nNIST SP 800 82r2: Guide to Industrial Control Systems (ICS) Security\nBaseline Security Recommendations for IoT\nIndustrial Internet of Things Volume G4: Security Framework\nISO/IEC 27001:2013 Information technology -- Security techniques -- Information security management systems -- Requirements\nNIST SP 800 53r4: Security and Privacy Controls for Federal Information Systems and Organizations\nISO/IEC 27002:2013 Information technology -- Security techniques -- Code of practice for information security controls\nIoT Security White Paper 2017\nDraft NISTIR 8228: Considerations for Managing Internet of Things (IoT) Cybersecurity and Privacy Risks', 'NIST\nOWASP (Open Web Application Security Project)\nNIST\nNIST\nENISA\nIIC (Industrial Internet Consortium)\nISO\nNIST\nISO\nHuawei\nNIST', 'https://nvlpubs.nist.gov/nistpubs/ir/2017/NIST.IR.8183.pdf\nhttps://www.owasp.org/index.php/IoT_Security_Guidance\nhttps://csrc.nist.gov/publications/detail/nistir/8200/draft\nhttps://nvlpubs.nist.gov/nistpubs/SpecialPublications/NIST.SP.800-82r2.pdf\nhttps://www.enisa.europa.eu/publications/baseline-security-recommendations-for-iot\nhttps://www.iiconsortium.org/pdf/IIC_PUB_G4_V1.00_PB.pdf\nhttps://www.iso.org/standard/54534.html\nhttps://nvlpubs.nist.gov/nistpubs/SpecialPublications/NIST.SP.800-53r4.pdf\nhttps://www.iso.org/standard/54533.html\nhttps://www.hu